function getExecAcList() {
  let acList = ysm.filter(o => o.openid).map((o, i) => {
    let data = o.domain.match(/^(https?:\/\/(.+?))\//)
    let acHeader = {
      "Origin": data[1],
      "Host": data[2],
      "User-Agent": o.ua,
      "Referer": `${o.domain}redirect/${o.secret}?openid=${o.openid}&redirect_flag=read`
    }
    return {
      no: i + 1,
      last_gold: 0,
      day_gold: 0,
      remain_read: 0,
      day_read: 0,
      openid: o.openid,
      domain: o.domain,
      headers: {
        ...baseHeaders,
        ...acHeader
      },
      body: `secret=${o.secret}&type=read`,
      txbody: o.txbody
    }
  })
  let execAcList = []
  let len = acList.length
  // è®¡ç®—åˆ†ç»„åæ¯ç»„è´¦å·ä¸ªæ•°
  let slot = len % concurrency == 0 ? len / concurrency : parseInt(len / concurrency) + 1
  slot = Math.ceil(len / (slot || 1))
  let idx = -1
  acList.forEach((o, i) => {
    if (i % slot == 0) {
      idx++
    }
    if (execAcList[idx]) {
      execAcList[idx].push(o)
    } else {
      execAcList[idx] = [o]
    }
  })
  $.log(`äº‘æ‰«ç å½“å‰è®¾ç½®çš„æç°é‡‘é¢ä¸º: ${ysmtxAmt / 10000} å…ƒ`, `----------- å…±${len}ä¸ªè´¦å·åˆ†${execAcList.length}ç»„å»æ‰§è¡Œ -----------`)
  return execAcList
}

