function ysm4(ac) {
  return new Promise((resolve) => {
    let opts = {
      url: `${ac.domain}gold?openid=${ac.openid}&time=${Date.parse(new Date())}`,
      headers: ac.headers
    }
    $.get(opts, (err, resp, data) => {
      try {
        if (err) {
          $.logErr(`âŒ è´¦å·${ac.no} APIè¯·æ±‚å¤±è´¥ï¼Œè¯·æ£€æŸ¥ç½‘ç»œåé‡è¯•\n url: ${opts.url} \n data: ${JSON.stringify(err, null, 2)}`)
        } else {
          const result = JSON.parse(data)
          if (result.errcode == 0 && result.data) {
            ac.last_gold = (result.data.last_gold || 0) - 0
            ac.day_read = (result.data.day_read || 0) - 0
            ac.day_gold = (result.data.day_gold || 0) - 0
            ac.remain_read = (result.data.remain_read || 0) - 0
          }
        }
      } catch (e) {
        $.logErr(`======== è´¦å· ${ac.no} ========\nurl: ${opts.url}\nerror:${e}\ndata: ${resp && resp.body}`)
      } finally {
        resolve()
      }
    })
  })
}

